namespace App\Repository;

use App\Entity\File;
use Doctrine\ORM\EntityRepository;

/**
 * FileRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom repository methods below.
 */
class FileRepository extends EntityRepository
{
    /**
     * @return array|null
     */
    public function getCoverFilesWithoutThumbnails()
    {
        $query = $this->createQueryBuilder('f');

        return $query->select('f')
            ->join('f.covers', 'cu')
            ->where('f.mimeType in (\'image/png\', \'image/jpeg\')')
            ->andWhere('f.thumbnail is null')
            ->groupBy('f.id')
            ->getQuery()
            ->getResult();
    }

    /**
     * @param int $count
     * @param File|null $fromFile
     * @return array|null
     */
    public function getImages(int $count = 10, File $fromFile = null)
    {
        if ($fromFile) {
            return $this->createQueryBuilder('f')
                ->select('f')
                ->join('f.contentUnits', 'cu')
                ->join('cu.channel', 'ch')
                ->where('f.mimeType != :mimeType')
                ->andWhere('ch.url is not null')
                ->andWhere('f.id < :fromId')
                ->setParameters(['mimeType' => 'text/html', 'fromId' => $fromFile->getId()])
                ->setMaxResults($count)
                ->orderBy('f.id', 'desc')
                ->groupBy('f')
                ->getQuery()
                ->getResult();
        } else {
            return $this->createQueryBuilder('f')
                ->select('f')
                ->join('f.contentUnits', 'cu')
                ->join('cu.channel', 'ch')
                ->where('f.mimeType != :mimeType')
                ->andWhere('ch.url is not null')
                ->setParameters(['mimeType' => 'text/html'])
                ->setMaxResults($count)
                ->orderBy('f.id', 'desc')
                ->groupBy('f')
                ->getQuery()
                ->getResult();
        }
    }

    /**
     * @param string $tag
     * @param int $count
     * @param File|null $fromFile
     * @return array|null
     */
    public function getImagesByTag(string $tag, int $count = 10, File $fromFile = null)
    {
        if ($fromFile) {
            return $this->createQueryBuilder('f')
                ->select('f')
                ->join('f.contentUnits', 'cu')
                ->join('cu.channel', 'ch')
                ->join('cu.tags', 'cut')
                ->join('cut.tag', 't')
                ->where('f.mimeType != :mimeType')
                ->andWhere('ch.url is not null')
                ->andWhere('t.name = :tag')
                ->andWhere('f.id < :fromId')
                ->setParameters(['mimeType' => 'text/html', 'fromId' => $fromFile->getId(), 'tag' => $tag])
                ->setMaxResults($count)
                ->orderBy('f.id', 'desc')
                ->groupBy('f')
                ->getQuery()
                ->getResult();
        } else {
            return $this->createQueryBuilder('f')
                ->select('f')
                ->join('f.contentUnits', 'cu')
                ->join('cu.channel', 'ch')
                ->join('cu.tags', 'cut')
                ->join('cut.tag', 't')
                ->where('f.mimeType != :mimeType')
                ->andWhere('ch.url is not null')
                ->andWhere('t.name = :tag')
                ->setParameters(['mimeType' => 'text/html', 'tag' => $tag])
                ->setMaxResults($count)
                ->orderBy('f.id', 'desc')
                ->groupBy('f')
                ->getQuery()
                ->getResult();
        }
    }
}
